// landing page slider
var slideIndex = 1;
showIndex(slideIndex);
function currentSlide(n){showIndex(slideIndex = n);}
setInterval(() => {
    showIndex(slideIndex++);
}, 5000);
function showIndex(n){
var i,slides = document.getElementsByClassName("landing_slides"), 
dots = document.getElementsByClassName("dot"); 
if(n > slides.length-1){slideIndex = 1}
if( n < 1){slideIndex = slides.length}
for(i=0; i<slides.length; i++){slides[i].style.display = "none";}
for(i=0; i<dots.length; i++){dots[i].className = dots[i].className.replace(" active_dot", "");}
slides[slideIndex-1].style.display = "block";
dots[slideIndex-1].className += " active_dot";
}
//end

